<?php// checking textfunction yuliza_test_input($data){    $data = trim($data);    $data = stripslashes($data);    $data = htmlspecialchars($data);    return $data;}// for messaging text checkingfunction message_input($data){    $data = trim($data);    $data = htmlspecialchars($data);    return $data;}// file download functionfunction universal_file_download(){    if (isset($_GET['file-download'])) {        if (!empty($_GET['file-name-download'])) {            $file             = $_GET['file-name-download'];            $file_to_download = ABSPATH . 'wp-content/uploads/' . $file; // file to be downloaded            header("Expires: 0");            header("Last-Modified: " . gmdate("D, d M Y H:i:s") . " GMT");            header("Cache-Control: no-store, no-cache, must-revalidate");            header("Cache-Control: post-check=0, pre-check=0", false);            header("Pragma: no-cache");            header("Content-type: application/file");            header('Content-length: ' . filesize($file_to_download));            header('Content-disposition: attachment; filename=' . basename($file_to_download));            $chunkSize = filesize($file_to_download);            $handle    = fopen($file_to_download, 'rb');            while (!feof($handle)) {                $buffer = fread($handle, $chunkSize);                echo $buffer;                ob_flush();                flush();            }            fclose($handle);            exit;        }    }}if (isset($_GET['page']) && ($_GET['page'] == 'yuliza-support' || $_GET['page'] == 'yuliza-support-user' || $_GET['page'] == 'yuliza-support-user-files' || $_GET['page'] == 'yuliza-support-user-files-sent' || $_GET['page'] == 'yuliza-support-user-messages')) {    add_action('admin_enqueue_scripts', 'universal_enqueue_styles');}function universal_enqueue_styles(){    wp_enqueue_script('admin_js', get_stylesheet_directory_uri() . '/js/jquery.min.js', false);    // For Child Theme    wp_enqueue_style('bootstrap_css', get_stylesheet_directory_uri() . '/css/bootstrap.css');    wp_enqueue_style('font_awesome', 'https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/css/font-awesome.css');    wp_enqueue_script('bootstrap_script', get_stylesheet_directory_uri() . '/js/bootstrap.min.js', array(), '4.0.0', true);}// Making User Role add_role('yuliza_user', __('Yuliza User', 'yuzliza user'));// Checking User Login//add_action('init', 'checking_user_access');function checking_user_access(){    global $current_user;    $redirect   = home_url('/');    $user_roles = $current_user->roles;    $user_role  = array_shift($user_roles);    if ($user_role === 'yuliza_user') {        exit(wp_redirect('https://www.google.com/'));    }}function my_login_redirect(){    global $current_user;    $redirect   = home_url('/');    $user_roles = $current_user->roles;    $user_role  = array_shift($user_roles);    if ($user_role === 'universal_tax_user') {        wp_redirect('https://www.google.com/');    } else {        return;    }}//add_filter('login_redirect', 'my_login_redirect');function create_wp_tables(){    global $wpdb;    $charset_collate = $wpdb->get_charset_collate();    require_once(ABSPATH . 'wp-admin/includes/upgrade.php');    //* Create the files table    $table_name = 'yuliza_files'; //$wpdb->prefix . 'univarsal_tax_files';    $sql        = "CREATE TABLE $table_name (	 id BIGINT(20) NOT NULL AUTO_INCREMENT,	 user_id BIGINT(20) NOT NULL,	 file_name VARCHAR(225),	 file_extension VARCHAR(225),	 file_size VARCHAR(50),	 file_location VARCHAR(225),	 new_file_status TINYINT,	 created_at TIMESTAMP,	 updated_at TIMESTAMP,	 status TINYINT,	 sender_id BIGINT(20),	 receiver_id BIGINT(20),	 PRIMARY KEY (id)	 ) $charset_collate;";    dbDelta($sql);    // Create universal messaging system    //* Create the teams table    $table_name_messaging = 'yuliza_messaging'; //$wpdb->prefix . 'yuliza_messaging';    $sql_messaging        = "CREATE TABLE $table_name_messaging (	 id BIGINT(20) NOT NULL AUTO_INCREMENT,	 message TEXT,	 sender_id BIGINT(20) NOT NULL,	 receiver_id BIGINT(20) NOT NULL,	 new_message_status TINYINT,	 created_at TIMESTAMP,	 updated_at TIMESTAMP,	 status TINYINT,	 PRIMARY KEY (id)	 ) $charset_collate;";    dbDelta($sql_messaging);}add_action('after_setup_theme', 'create_wp_tables');// Adding Menu to admin dashboard add_action('admin_menu', 'wpse_91693_register');function wpse_91693_register(){    // Universal tax main dashboard    add_menu_page('Yuliza Support', // page title        'Yuliza Support', // menu title        'manage_options', // capability            //'universal-tax',     // menu slug        'yuliza-support', // menu slug            //'universal_tax_mainpage', // callback function        'yuliza_support_mainpage', //// callback function        'dashicons-admin-site', // mene icons        90);    // Universal user     add_menu_page('Universal Tax User', // page title        'Universal Tax User', // menu title        'manage_options', // capability        'yuliza-support-user', // menu slug        'yuliza_support_user', // callback function        'dashicons-admin-site', // mene icons        120);    // Universal user files    add_menu_page('UTax User Files', // page title        'UTax User Files', // menu title        'manage_options', // capability        'yuliza-support-user-files', 'yuliza_support_user_files', // callback function        'dashicons-admin-site', // mene icons        140);    // Universal user files    add_menu_page('UTax User Files Sent', // page title        'UTax User Files Sent', // menu title        'manage_options', // capability        'yuliza-support-user-files-sent', // menu slug        'yuliza_support_user_files_sent', // callback function        'dashicons-admin-site', // mene icons        160);    // Universal user messages    add_menu_page('UTax User Messages', // page title        'UTax User Messages', // menu title        'manage_options', // capability        'yuliza-support-user-messages', // menu slug        'yuliza_support_user_messages', // callback function        'dashicons-admin-site', // mene icons        180);    // remove universal tax user page    remove_menu_page('yuliza-support-user');    // remove universal tax user files page    remove_menu_page('yuliza-support-user-files');    // remove universal tax user files page    remove_menu_page('yuliza-support-user-files-sent');    // remove universal tax user messages page    remove_menu_page('yuliza-support-user-messages');}// universal taxt user files sent callbackfunction yuliza_support_user_files_sent(){    $file = get_template_directory() . '-child/inc/universal-tax-user-files-sent.php';    if (file_exists($file)) {        require get_template_directory() . '-child/inc/universal-tax-user-files-sent.php';    } else {        echo "not exist";    }}// universal tax user messages callbackfunction yuliza_support_user_messages(){    $file = get_template_directory() . '-child/inc/universal-tax-user-messages.php';    if (file_exists($file)) {        require get_template_directory() . '-child/inc/universal-tax-user-messages.php';    } else {        echo "not exist";    }}// universal tax user files callbackfunction yuliza_support_user_files(){    $file = get_template_directory() . '-child/inc/universal-tax-user-files.php';    if (file_exists($file)) {        require get_template_directory() . '-child/inc/universal-tax-user-files.php';    } else {        echo "not exist";    }}// universal tax mainpage callbackfunction yuliza_support_mainpage(){    $file = get_template_directory() . '-child/inc/included.php';    if (file_exists($file)) {        require get_template_directory() . '-child/inc/included.php';    } else {        echo "not exist";    }}// Adding Sub menu page view userfunction yuliza_support_user(){    $file = get_template_directory() . '-child/inc/universal-tax-user.php';    if (file_exists($file)) {        require get_template_directory() . '-child/inc/universal-tax-user.php';    } else {        echo "not exist";    }}