<div class="container-fluid">   <div class="row">      <div class="col">      </div>   </div>   <div class="row">      <div class="col">         <h2>USER SYSTEM</h2>      </div>   </div>   <div class="row">      <div class="col">      </div>   </div>   <div class="row">      <div class="col">         <table class="table table-hover">            <thead>               <tr>                  <th scope="col">#</th>                  <th scope="col">Name</th>                  <th scope="col">Status</th>                  <th scope="col">View Profile</th>                  <th scope="col">New Messages</th>                  <th scope="col">New Files</th>               </tr>            </thead>            <tbody>              <?php              $args_count = array(                  'role' => 'user_system'              );              $universal_tax_users_counting = new WP_User_Query($args_count);              $users_count                  = $universal_tax_users_counting->get_results();              $users_count                  = count($users_count); // total users from universal tax user              $total_users_per_page         = 10;              $page                         = isset($_GET['uni-user']) ? abs((int) $_GET['uni-user']) : 1;              $totalPage                    = ceil($users_count / $total_users_per_page);              $offset                       = ($page * $total_users_per_page) - $total_users_per_page;              $args                         = array(                  'role' => 'user_system',                  'offset' => $offset,                  'number' => $total_users_per_page              );              //  table-active table-danger table-success              $universal_tax_users          = new WP_User_Query($args);              $users                        = $universal_tax_users->get_results();              foreach ($users as $user) {                  $user_login  = $user->data->user_login;                  $user_status = get_user_meta($user->ID, 'user_system_user_status', true);                  if (empty($user_status)) {                      $user_role_available = false;                  } else {                      $user_role_available = true;                      if ($user_status == 2) {                          $ustatus     = 'table-success';                          $ustatus_msg = "Need Approval";                      } elseif ($user_status == 1) {                          $ustatus     = 'table-active';                          $ustatus_msg = "Active";                      } elseif ($user_status == 3) {                          $ustatus     = 'table-danger';                          $ustatus_msg = "Disabled";                      }                  }                  if ($user_role_available == true) {                    ?>              <tr class="<?php echo $ustatus;?>">                <th scope="row">                  <?php echo $offset + 1;                        $offset++;                   ?>                                    </th>                <td><?php echo $user_login; ?></td>                <td><?php echo $ustatus_msg; ?></td>                <td>                  <form>                    <input type="hidden" name="page" value="user-system-user">                    <input type="hidden" name="user_id" value="<?php echo $user->ID; ?>">                    <button type="submit" class="btn btn-info">View</button>                  </form>                </td>                <?php                    global $wpdb;                                     $table = 'user_system_messaging';                     $query  = "SELECT * FROM " . $table . " WHERE sender_id =" . $user->ID . " AND receiver_id=1001 AND new_message_status=0";                    $total_query    = "SELECT COUNT(*) FROM " . $table . " WHERE sender_id =" . $user->ID . " AND receiver_id=1001 AND new_message_status=0";                    $total_new_msgs = $wpdb->get_var($total_query);                    //var_dump($total_query);                    if ($total_new_msgs > 0) {                        $new_messages = $total_new_msgs;                    } else {                        $new_messages = 0;                    }                    // for files                     $table_files       = 'user_system_files'; //$wpdb->prefix.'yuliza_files';                    $query_files       = "SELECT * FROM " . $table_files . " WHERE sender_id =" . $user->ID . " AND receiver_id=1001 AND new_file_status=0";                    $total_query_files = "SELECT COUNT(*) FROM " . $table_files . " WHERE sender_id =" . $user->ID . " AND receiver_id=1001 AND new_file_status=0";                    $total_new_files   = $wpdb->get_var($total_query_files);                    if ($total_new_files > 0) {                        $new_files = $total_new_files;                    } else {                        $new_files = 0;                    }?>                <td> <b> <?php echo $new_messages; ?></b></td>                <td> <b> <?php echo $new_files; ?></b></td>            </tr>           <?php                  }              } ?>            </tbody>            <tfoot>               <tr>                  <th scope="col">#</th>                  <th scope="col">Name</th>                  <th scope="col">Status</th>                  <th scope="col">View Profile</th>                  <th scope="col">New Messages</th>                  <th scope="col">New Files</th>               </tr>            </tfoot>         </table>      </div>   </div>    <div class="row">    <div class="col">      <?php      if ($totalPage > 1) {        $customPagHTML = '<div><span>Page ' . $page . ' of ' . $totalPage . '</span><span>&nbsp;&nbsp;&nbsp;</span>' . paginate_links(array(            'base' => add_query_arg('uni-user', '%#%'),            'format' => '',            'prev_text' => __('&laquo;'),            'next_text' => __('&raquo;'),            'total' => $totalPage,            'current' => $page          )) . '</div>';      }      echo $customPagHTML;      ?>    </div>    </div></div>