<?php   if (isset($_GET['user_id'])) {       $page              = 'user-system';       $user_id           = $_GET['user_id'];       $availability_user = get_user_meta($user_id, 'user_system_user_status', true);       if (empty($availability_user)) {           wp_redirect(admin_url('admin.php?page=' . $page));           exit();       }   } else {       $page = 'user-system';       wp_redirect(admin_url('admin.php?page=' . $page));       exit();   }   //user files   if (isset($_GET['user_files'])) {       $page    = $_GET['page'];       $user_id = $_GET['user_id'];       wp_redirect(admin_url('admin.php?page=' . $page . '&user_id=' . $user_id));       exit();   }   // files upload   if (isset($_POST['user_system_files_submitted'])) {       $errors            = 0;       $countfiles        = count($_FILES['user_system_files']['name']);       $check_file_exists = $_FILES['user_system_files']['name'][0];       if (empty($check_file_exists)) {           $fileErr = "Kindly Select a file";           $errors += 1;       }       if ($countfiles > 10) {           $fileErr = "Can't select files more than 10. You selected $countfiles  files.";           $errors += 1;       }       if ($errors == 0) {           $folder_location = ABSPATH . 'wp-content/uploads/user_system_admin';           $folder_created  = wp_mkdir_p($folder_location);           if ($folder_created == true) {               date_default_timezone_set("America/New_York");               for ($file_looping = 0; $file_looping < $countfiles; $file_looping++) {                   //$original_name = array_shift($_FILES['universal_tax_files']['name']);                   $original_name      = $_FILES['user_system_files']['name'][$file_looping];                   $attach_unique_name = uniqid(rand(), true);                   $new_name           = $attach_unique_name . '_' . $original_name;                   // Now we'll move the temporary file to this plugin's uploads directory.                   //$source = array_shift($_FILES['universal_tax_files']['tmp_name']);                   $source             = $_FILES['user_system_files']['tmp_name'][$file_looping];                   $destination        = $folder_location . '/' . $new_name;                   //$destination = $folder_location.'/'.$_FILES['universal_tax_files']['name'];                   $file_status        = move_uploaded_file($source, $destination);                   $path_parts         = pathinfo($original_name);                   //file extension                   $fileExtension      = $path_parts['extension'];                   $file_date          = date("Y-m-d h:i:s");                   if ($file_status == true) {                       global $wpdb;                       $tablename          = 'user_system_files'; //$wpdb->prefix.'univarsal_tax_files';                       $staus_table_insert = $wpdb->insert($tablename, array(                           'user_id' => $_POST['user_id'],                           'file_name' => $new_name,                           'file_extension' => $fileExtension,                           'file_size' => $_FILES['user_system_files']['size'][$file_looping],                           'file_location' => $current_user->data->user_email,                           'created_at' => $file_date,                           'status' => 1,                           'sender_id' => 1001,                           'receiver_id' => $_POST['user_id'],                           'new_file_status' => 0                       ), array(                           '%d',                           '%s',                           '%s',                           '%s',                           '%s',                           '%s',                           '%d',                           '%d',                           '%d',                           '%d'                       ));                       if ($staus_table_insert != false) {                           $fileErr = "Data Successfully Inserted";                       }                   } else {                       $fileErr = "File not uploaded";                   }               }           } else {               $fileErr = "Error in creating folder";           }       }   }   // user messages   if (isset($_GET['universal_user'])) {       $errors          = 0;       $current_user_id = (int) $_GET['user_id']; // user id       if (empty($_GET["user_email"])) {           $emailErr = "Email is required";           $errors += 1;       } else {           $user_email = user_system_test_input($_GET["user_email"]);           // check if e-mail address is well-formed           if (!filter_var($user_email, FILTER_VALIDATE_EMAIL)) {               $emailErr = "Invalid email format";               $errors += 1;           }           if (email_exists($user_email) != false) {               $uid = email_exists($user_email);               if ($uid == $current_user_id) {                   $user_id = wp_update_user(array(                       'ID' => $current_user_id,                       'user_email' => $user_email                   ));                   if (is_wp_error($user_id)) {                       $emailErr = "Email is not updated.";                       $errors += 1;                   }               }           } else {               $user_id = wp_update_user(array(                   'ID' => $current_user_id,                   'user_email' => $user_email               ));               if (is_wp_error($user_id)) {                   $emailErr = "Email is not updated.";                   $errors += 1;               } else {                   $emailErr = "Email is updated.";               }           }       }       // Now user status       if (!empty($_GET['user_status'])) {           $user_status = update_user_meta($current_user_id, 'user_system_user_status', $_GET['user_status']);           if ($user_status == false) {               $ustatusErr = "User Status is not updated.";               $errors += 1;           }       }   }   ?><div class="container-fluid">   <div class="row">      <div class="col">      </div>   </div>   <div class="row">      <div class="col">         <h2>User System - User Info</h2>      </div>   </div>   <div class="row">      <div class="col">         <?php            $universal_user = get_userdata($_GET['user_id']);            if ($universal_user == false) {                echo "<h3>Sorry, This user does not exist.</h3>";            } else {                $universal_user_status = get_user_meta($universal_user->ID, 'user_system_user_status', true);            ?>         <form class="form-horizontal">            <input type="hidden" name="page" value="user-system-user">            <input type="hidden" name="user_id" value="<?php               echo $universal_user->ID;               ?>">            <fieldset>               <legend></legend>               <div class="form-group">                  <label for="user_login" class="col-lg-2 control-label">User Login</label>                  <div class="col-lg-10">                     <input type="text" class="form-control" id="user_login" name="user_login" placeholder="Enter User Login" value="<?php                        echo $universal_user->data->user_login;                        ?>" disabled>                  </div>                  <span><?php                     echo isset($nameErr) ? $nameErr : '';                     ?></span>               </div>               <div class="form-group">                  <label for="user_email" class="col-lg-2 control-label">User Email</label>                  <div class="col-lg-10">                     <input type="email" class="form-control" id="user_email" name="user_email" placeholder="Enter User Email" value="<?php                        echo $universal_user->data->user_email;                     