    ?>                     </select>                  </div>                  <span><?php                     echo isset($ustatusErr) ? $ustatusErr : '';                     ?></span>               </div>               <input type="hidden" name="universal_user">               <div class="form-group">                  <div class="col-lg-10 col-lg-offset-2">                     <button type="submit" class="btn btn-primary">Submit</button>                  </div>               </div>            </fieldset>         </form>         <?php            }            ?>      </div>   </div>   <br>   <div class="row">      <div class="col">         <h1>Send files to <?php            echo $universal_user->data->user_login;            ?></h1>      </div>   </div>   <br>   <div class="row">      <div class="col-3">         <form method="post" enctype="multipart/form-data">              Choose Files (Not more than 10) : <input type="file" id="user_system_files" name="user_system_files[]" multiple="multiple">            <br>            <input type="hidden" name="user_id" value="<?php               echo $universal_user->ID;               ?>">            <input type="hidden" name="user_system_files_submitted">            <br>            <span><?php               echo isset($fileErr) ? $fileErr : '';               ?></span>            <br>            <button type="submit" class="btn btn-outline-primary">Submit</button>         </form>      </div>      <div class="col-9"></div>   </div>   <br>   <div class="row">      <div class="col">         <h1><?php            echo $universal_user->data->user_login;            ?> Files & Messages</h1>      </div>   </div>   <br>   <div class="row">      <div class="col-3">      </div>      <div class="col-3">         <form>            <input type="hidden" name="page" value="user-system-files">            <input type="hidden" name="user_id" value="<?php               echo $universal_user->ID;               ?>">            <input type="hidden" name="user_files">            <button type="submit" class="btn btn-outline-primary">Files</button>         </form>      </div>      <div class="col-3">         <form>            <input type="hidden" name="page" value="user-system-files-sent">            <input type="hidden" name="user_id" value="<?php               echo $universal_user->ID;               ?>">            <input type="hidden" name="user_files">            <button type="submit" class="btn btn-outline-info">Sent Files</button>         </form>      </div>      <div class="col-3">         <form>            <input type="hidden" name="page" value="user-system-messages">            <input type="hidden" name="user_id" value="<?php               echo $universal_user->ID;               ?>">            <input type="hidden" name="user_messages">            <button type="submit" class="btn btn-outline-warning">Messages</button>         </form>      </div>   </div></div>