<?php$current_user = wp_get_current_user();if ($current_user->ID == 0) {    $redirect_url = home_url('/user-system-login/');    wp_redirect($redirect_url);    exit();} else if ($current_user->ID != 0) {    $user_role = $current_user->roles[0];    if ($user_role == 'administrator') {        $redirect_url = home_url();        wp_redirect($redirect_url);        exit();    }}if (isset($_GET['user_edit_profile'])) {    $errors     = 0;    $first_name = trim($_GET['client_fname']);    $last_name  = trim($_GET['client_lname']);    $phone      = trim($_GET['client_phone']);    $email      = trim($_GET['client_email']);    $password   = trim($_GET['client_password']);    $user_id    = $_GET['user_id'];    //echo $first_name." ".$last_name." ".$phone." ".$email." ".$password;     if (empty($first_name)) {        $fnameErr = "Kindly enter first name";        $errors += 1;    }    if (empty($last_name)) {        $lnameErr = "Kindly enter last name";        $errors += 1;    }    if (empty($phone)) {        $phoneErr = "Kindly enter phone number";        $errors += 1;    }    if (empty($email)) {        $emailErr = "Kindly enter email address";        $errors += 1;    }    if ($errors == 0) {        update_user_meta($user_id, 'first_name', $first_name);        update_user_meta($user_id, 'last_name', $last_name);        update_user_meta($user_id, 'phone', $phone);        if (!empty($password)) {            $user_idp = wp_update_user(array(                'ID' => $user_id,                'user_pass' => $password            ));            if (is_wp_error($user_idp)) {                $err_field = "Oops ! Error , password is not updated.";            } else {                $success_field = "User Updated Successfully...!!!!";            }        }        $user_ide = wp_update_user(array(            'ID' => $user_id,            'user_email' => $email        ));        if (is_wp_error($user_ide)) {            $err_field = "Oops ! Error , email is not updated.";        } else {            $success_field = "User Updated Successfully...!!!!";        }    }}$fname = get_user_meta($current_user->ID, 'first_name', true);$lname = get_user_meta($current_user->ID, 'last_name', true);$phone = get_user_meta($current_user->ID, 'phone', true);$email = $current_user->user_email;?><div id="register_area">   <h1>Edit profile</h1>   <span class="err-field"><?php echo isset($err_field) ? $err_field : ''; ?></span>   <span class="success-field"><?php echo isset($success_field) ? $success_field : ''; ?></span>   <form>      <div class="filed">         <input type="text" name="client_fname" placeholder="First name" value="<?php echo $fname; ?>">         <span class="err-field"><?php echo isset($fnameErr) ? $fnameErr : ''; ?></span>      </div>      <div class="filed">         <input type="text" name="client_lname" placeholder="Last name" value="<?php echo $lname; ?>">         <span class="err-field"><?php echo isset($lnameErr) ? $lnameErr : ''; ?></span>      </div>      <div class="filed">         <input type="text" name="client_phone" placeholder="Phone" value="<?php echo $phone; ?>">         <span class="err-field"><?php echo isset($phoneErr) ? $phoneErr : ''; ?></span>      </div>      <div class="filed">         <input type="email" name="client_email" placeholder="Email address" value="<?php echo $email; ?>">         <span class="err-field"><?php echo isset($emailErr) ? $emailErr : ''; ?></span>      </div>      <div class="filed">         <input type="password" name="client_password" placeholder="Password">         <span class="err-field"><?php echo isset($passwordErr) ? $passwordErr : ''; ?></span>      </div>      <input type="hidden" name="user_id" value="<?php echo $current_user->ID; ?>">      <input type="hidden" name="user_edit_profile">      <input type="submit" class="button-submit" value="Submit">   </form></div>